  def RGBToPyCmap(rgbdata):
      nsteps = rgbdata.shape[0]
      stepaxis = np.linspace(0, 1, nsteps)
      rdata=[]; gdata=[]; bdata=[]
      for istep in range(nsteps):
          r = rgbdata[istep,0]
          g = rgbdata[istep,1]
          b = rgbdata[istep,2]
          rdata.append((stepaxis[istep], r, r))
          gdata.append((stepaxis[istep], g, g))
          bdata.append((stepaxis[istep], b, b))
      mpl_data = {'red':   rdata,
                   'green': gdata,
                   'blue':  bdata}
      return mpl_data

